mapper:













        select o.id id,o.code code,o.status status,c.name cname,o.number number,o.totalprice totalprice,a.getaddress agetaddress,u.name uname,a.receivephone areceivephone,l.company lcompany
        from shop_commodity c,shop_order o,shop_address a,shop_user u,shop_logistic l,shop_co co
        where co.order_id=c.id
        and o.address_id=a.id and o.user_id=u.id and o.logistic_id=l.id
        and o.id=#{id}





        select commodity_id from shop_co where order_id=#{order_id}


        select * from shop_commodity where id={id}

`   
        update shop_order o,shop_address a
        set
        where o.id = #{id}
         and o.address_id=a.id
